  --foreground: oklch(0.16 0.018 260);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.16 0.018 260);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.16 0.018 260);

  /* Primary — deep indigo */
  --primary: oklch(0.38 0.14 265);
  --primary-foreground: oklch(0.98 0 0);

  --secondary: oklch(0.955 0.008 260);
  --secondary-foreground: oklch(0.25 0.03 260);
  --muted: oklch(0.955 0.005 250);
  --muted-foreground: oklch(0.52 0.02 260);
  --accent: oklch(0.945 0.012 260);
  --accent-foreground: oklch(0.22 0.03 260);

  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.007 260);
  --input: oklch(0.93 0.006 260);
  --ring: oklch(0.55 0.12 265);

  /* Charts */
  --chart-1: oklch(0.62 0.19 265);
  --chart-2: oklch(0.7 0.18 50);
  --chart-3: oklch(0.6 0.14 185);
  --chart-4: oklch(0.65 0.17 310);
  --chart-5: oklch(0.68 0.15 150);

  /* Sidebar — deep navy */
  --sidebar: oklch(0.2 0.04 265);
  --sidebar-foreground: oklch(0.92 0.01 260);
  --sidebar-primary: oklch(0.72 0.17 55);
  --sidebar-primary-foreground: oklch(0.15 0.03 260);
  --sidebar-accent: oklch(0.27 0.04 265);
  --sidebar-accent-foreground: oklch(0.92 0.01 260);
  --sidebar-border: oklch(0.28 0.035 265);
  --sidebar-ring: oklch(0.55 0.14 265);

  /* Custom tokens */
  --hero: oklch(0.38 0.14 265);
  --hero-foreground: oklch(0.98 0 0);
