          unbezahlt,
          umsatz,
          naechsteKurse,
          anmeldungenListe: anmeldungenList.slice(0, 6),
          kurseList,
        });
      } finally {
        setLoading(false);
      }
    }
    load();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
